import React, { Component } from 'react'

export class index extends Component {
  constructor(){
    super()
    this.multiplevalue=[
{id:1,cityname:"islamabad"},
{id:2,cityname:"Lahore"},
{id:3,cityname:"Mansehra"},
{id:4,cityname:"Abbottabad"}]

    this.state={
      multiplevalue:this.multiplevalue,
      multipleitem:[]
    }
  }
  checkitem=(index,value)=>{
const itemselect=[...this.state.multipleitem]
const model=[...this.state.multiplevalue]
const indexs=model.indexOf(value)
model[indexs].ischeck=!model[indexs].ischeck

if(model[indexs].ischeck===true){
  itemselect.push(value)
}
else{
  itemselect.pop(value)
}


this.setState({
  multiplevalue:model,
  multipleitem:itemselect
},()=>{
  console.log("slected value",this.state.multipleitem)
})

  }

  allcheckbox=(e)=>{
    const model=[...this.state.multiplevalue]
    const itemselect=[...this.state.multipleitem]
    

    model.map((value)=>{
      value.ischeck=!value.ischeck
    })

    this.setState({
      multiplevalue: model 
    },()=>{
      this.state.multiplevalue.map((value)=>{
        itemselect.push(value)
      },this.setState({
        multipleitem:itemselect
      }))
    })
    console.log("selected value",this.state.multipleitem)
  }
  render() {
    return (
      <div style={{marginTop:"60px"}}>
      <input type="checkbox" name="parent" id="parent" 
      onClick={this.allcheckbox} 
      checked={this.state.multiplevalue.every(user=>user.ischeck===true)}/>
     <label htmlFor="parent">all checkbox</label>
        <br/>
        <br/>
        <br/>
        {
          this.state.multiplevalue.map((value,index)=>{
            return <React.Fragment key={index}>   
     <input type="checkbox" name={value.cityname} 
     id={value.cityname} 
     onClick={()=>this.checkitem(index,value)} 
     checked={value.ischeck?true:false} />

     <label htmlFor={value.cityname}>{value.cityname}</label>
     <br/>
            </React.Fragment> 
          })
        }
      </div>
    )
  }
}

export default index
