                age: 50,
                eyecolor: "blue"
              }
        }
    }
    addnewvalue=()=>{
     const persondata={...this.state.person}   
     persondata.nationality="pakistani"
     persondata.city="Islamabad"
    this.setState({
  person:persondata
    })
      
    }
    deletevalue=()=>{
        const persondata={...this.state.person} 
        delete persondata.nationality
        this.setState({
            person:persondata
