import React, { Component } from 'react'

export class JsObject extends Component {
    constructor(){
        super()
        this.state={
            person :{
                firstname: "John",
                lastname: "Doe",
                age: 50,
                eyecolor: "blue"
              }
        }
    }
    addnewvalue=()=>{
     const persondata={...this.person}   
     persondata.nationality="pakistani"

     this.setState({
       person: persondata
     },()=>{
         console.log("persondata",this.state.person)
     })
    }
    render() {
        const {person}=this.state
        return (
            <div>
            <p>
                {
                  Object.values(person).map(value=>{
                      return <>
                      {
                          <span>{value}</span>
                      }<br/>
                      </>
                  })
                }
                <button onClick={this.addnewvalue}>
                    add new value
                </button>
            </p>
            </div>
        )
    }
}

export default JsObject
