import React, { Component } from 'react'
import axios from 'axios'
var NpmApi = require('npm-api');
export class fileupload extends Component {
    state={
textfile:'',
ipdaddress:"",
hostname:"",
systemhostname:"",
systemhostname2:""

    }
    uploadfile=(e)=>{
        let file=e.target.files;
        let reder=new FileReader();
        reder.readAsText(file[0]);
        reder.onload=e=>{
            // const file=e.target.result.split(/\r\n|\n/).join('\n');
            this.setState({
                textfile:e.target.result
            })
        }
       
        // console.log("files",files)
    }
  
    GetComputerName=()=>{
        let npm = new NpmApi();
        var view = npm.view('byUser');
       var hostname= window.location.hostname
        console.log("view",view,"hostname",hostname)
        this.setState({
            hostname:view  
        })
      
    }
    getData = async () => {
        const res = await axios.get('https://geolocation-db.com/json/')
        console.log(res.data);
        var host = window.location.protocol + "//" + window.location.host;
        
        this.setState({
            ipdaddress:res.data.IPv4,
            hostname:host
        })
 
        
      }
    render() {
        const {textfile}=this.state
        return (
            <div>

        {/* <div>
            <h>File Upload</h>
            <br/>
            <br/>
            <div>
                <input type="file" onChange={(e)=>this.uploadfile(e)}/>

            </div>
            <br/>
            <hr style={{border:"1.5px solid  green"}}/>
            <h1>Base 64</h1>
                         <textarea
                            className="form-control"
                            name="comments"
                            value={textfile}
                            // id={index}
                            // value={row.comments}
                            // onChange={(e) => this.handlePatContChange(e)}
                            rows={10}
                            col={1}
                          />
        </div> */}

        <div>
            <h1>get IP Address   {this.state.ipdaddress} ,,,, host name={this.state.hostname},,,,system host name={this.state.systemhostname2},,,system host name2{this.state.systemhostname}</h1> 
            <button onClick={this.getData}>get ipaddress</button>

            <button onClick={this.GetComputerName}>get name</button>
        </div>
        </div>
    )
}         
    
}

export default fileupload
