import React,{useState}  from 'react'
import Swal from 'sweetalert2'
function Slectmultiplecheckboxes() {

const [mailAllArrayIDsToDelete,setmailAllArrayIDsToDelete]=useState({})
const [checkedAllMail, setcheckedAllMail ]=useState(false)

const multiplecheck=[
    {id:1,city:"Karachi"},
    {id:2,city:"Islamabad"},
    {id:3,city:"Lahore"},
    {id:4,city:"Rawalpindi"},
    {id:5,city:"Peshawar"},
    {id:6,city:"Faisalabad"},
    {id:7,city:"Multan"},
]
const handlechange=()=>{


}
const handleslectall= async()=>{

await setcheckedAllMail(!checkedAllMail)
let arr=[]
if(checkedAllMail){
    await multiplecheck.map((value,index)=>{
       return arr.push(value.id)
    })
    setmailAllArrayIDsToDelete(
     {mailAllArrayIDsToDelete:arr}
    )
}
else{
    setmailAllArrayIDsToDelete({
        mailAllArrayIDsToDelete:[] 
    })
}


}

 const DeleteAllRecourd=async()=>{

}

const deleteSingleRecourd=()=>{
    Swal.fire("delete recourd")
}
    return (
        <div>
               {/* parentCheckboxes */}

            <div  className="d-inline-flex w-25">
           
           <input type="checkbox" id="allslect"
           disabled={multiplecheck.length>0?false:true}
            name="allselectbox" 
            checked={checkedAllMail}
            onchange={handleslectall}/>

             <label for="allslect">All Select Boxes</label>

             <button className="btn bg-info" onClick={DeleteAllRecourd}>Delete All</button>
            </div>
                <div className=" d-flex justify-content-center w-75">
                <table  className="table table-center ">
                    <thead><tr>
                        <th></th>
                        <th> City Name</th>
                        <th> Action</th>
                        </tr></thead>
                        <tbody>
                            {
                               multiplecheck.map((row,inde)=>{
                                   return <tr>
                                      
                           <td>
                            {/* Child CheckBoxes */}

                                <input type="checkbox" id="row.city"
                                name="row.city" onChange={()=>handlechange(row)} /> 
                           </td>
                           <td> <label for="row.city">{row.city}</label></td>
                           <td><button className="btn text-info bg-secondary" onClick={deleteSingleRecourd}>Delete Checkboxes</button> </td>
                         
                                   </tr>
                               }) 
                            }
                        </tbody>
                </table >
                </div>
            </div>
  
    )
}

export default Slectmultiplecheckboxes
