import React, { Component } from 'react'

export class SlectmultipleSlectClass extends Component {
    constructor(){
        super()
     this.state={
            checkedAllMail:false,
            mailAllArrayIDsToDelete:[]
        }
    }
    handleslectall=()=>{
        
    }
    render() {
    
        const multiplecheck=[
            {id:1,city:"Karachi"},
            {id:2,city:"Islamabad"},
            {id:3,city:"Lahore"},
            {id:4,city:"Rawalpindi"},
            {id:5,city:"Peshawar"},
            {id:6,city:"Faisalabad"},
            {id:7,city:"Multan"},
        ]
        const { checkedAllMail,mailAllArrayIDsToDelete } =this.state
        return (
            <div>
            {/* parentCheckboxes */}

         <div  className="d-inline-flex w-25">
        
        <input type="checkbox"
         id="allslect"
        disabled={multiplecheck.length>0?false:true}
         name="allslect" 
         checked={checkedAllMail}
         onChange={this.handleslectall}/>

          <label for="allslect">All Select Boxes</label>

          <button className="btn bg-info" onClick={DeleteAllRecourd}>Delete All</button>
         </div>
             <div className=" d-flex justify-content-center w-75">
             <table  className="table table-center ">
                 <thead><tr>
                     <th></th>
                     <th> City Name</th>
                     <th> Action</th>
                     </tr></thead>
                     <tbody>
                         {
                            multiplecheck.map((row,k)=>{
                             let check = false
                          if(mailAllArrayIDsToDelete.length>0){
                             mailAllArrayIDsToDelete.map(el=>{
                                 if(el===row.id) 
                                 return (check=true)
                                 
                                    })
                              
                          }
                            
                             
                                return (<tr key={k}>
                                   
                        <td>
                         {/* Child CheckBoxes */}

                             <input type="checkbox"
                             id={row.id}
                             name="row.city" 
                             checked={check}
                             onChange={()=>handleSinglechange(row.id)} /> 
                        </td>
                        <td> <label for="row.city">{row.city}</label></td>
                        <td><button className="btn text-info bg-secondary" onClick={deleteSingleRecourd}>Delete Checkboxes</button> </td>
                      
                                </tr>)
                            }) 
                         }
                     </tbody>
             </table >
             </div>
         </div>
        )
    }
}

export default SlectmultipleSlectClass
