import React, { Component } from 'react'
import img1 from './img1.jpg'

export class DragandDrop extends Component {

    drop=(ev)=>{
        ev.dataTransfer.setData("text", ev.target.id);
    }

    allowDrop=(ev)=>{
        ev.preventDefault();
    

    }
    drop=(ev)=>{
        ev.preventDefault();
        var data = ev.dataTransfer.getData("text");
        ev.target.appendChild(document.getElementById(data));
    }
   

    render() {
        return (
            <div>
                <h1>Drag and Drop</h1>

                <div id="div1" onDrop={(event)=>this.drop(event)} onDragOver={(event)=>this.allowDrop(event)} 
                style={{width:"350px",height:"70px",padding:"10px",border:"1px solid #aaaaaa",marginLeft:"600px"}}></div>
<br/>
<img id="drag1" src={img1} draggable="true" onDragStart={(event)=>this.drag(event)} width="336" height="69"/>

                
            </div>
        )
    }
}

export default DragandDrop
