import React, { Component } from 'react'
import Basicjavascript from './Basicjavascript'
import OOPCONCEPT from './Oop'
import Promise from './promise'
import AsyncFunction from './AsyncFunction'
import JsObject from './JsObject'
import DragandDrop from './DragandDrop'
import Symbel from './Symbel'
import ReactDnd from './React Dnd/index'
export class Index extends Component {
constructor(){
    super()
    this.state={
        activetab:0
    } 
}

activetabfun=(e,tabid)=>{
    this.setState({
        activetab:tabid
    })

}
    render() {

        const {activetab}=this.state
        return (
        <>
            <div >
                
                <h1> Advanced Javascript</h1>
         <button className="btn btn-secondary mr-3 " 
         onClick={(e)=>this.activetabfun(e,1)} style={{marginRight:"20px"}}> Basic   </button> 


   <button className="btn btn-secondary ml-2" 
         onClick={(e)=>this.activetabfun(e,2)} style={{marginRight:"20px"}}>OOP Concept </button>

<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,3)} style={{marginRight:"20px"}}>Promise </button>

<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,4)} style={{marginRight:"20px"}}>AsynC Function </button>


         
<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,5)} style={{marginRight:"20px"}}>Js Object </button>

<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,6)} style={{marginRight:"20px"}}>DragandDrop </button>


<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,7)} style={{marginRight:"20px"}}>Symbols </button>

         
<button className="btn btn-secondary ml-2"  
         onClick={(e)=>this.activetabfun(e,7)} style={{marginRight:"20px"}}>React Dnd </button>

         
            </div>
<br/>




{activetab===1&&
   <Basicjavascript/>
}
{activetab===2&&
    <OOPCONCEPT/>
}
{   activetab===3&&
    <Promise/>
}
{
 activetab===4&&  
 <AsyncFunction/> 
}

{
 activetab===5&&  
 <JsObject/> 
}
{
 activetab===6&&  
 <DragandDrop/> 
}

{
 activetab===7&&  
 <Symbel/> 
}

{
 activetab===8&&  
 <ReactDnd/> 
}




</>

        )
    }
}

export default Index
