import React,{useState,useEffect} from 'react'
let multiplecheck=[
    {id:1,city:"Karachi"},
    {id:2,city:"Islamabad"},
    {id:3,city:"Lahore"},
    {id:4,city:"Rawalpindi"},
    {id:5,city:"Peshawar"},
    {id:6,city:"Faisalabad"},
    {id:7,city:"Multan"},
]
function Selectmultiplecheckboxes() {
    const [user,setuser]=useState([])

    useEffect(()=>{
        setuser(multiplecheck)
    },[])

    const handleselect =(e)=>{
        const {name,checked}=e.target

if(name==="allselect"){
    let tempuser=user.map(users=>{
        return {...users,ischecked:checked}
    })
    setuser(tempuser)
    console.log("data after",multiplecheck)
}

else{
    let tempuser=user.map(users=>
        users.name===name? {...users,ischecked:checked}:users
    )
    setuser(tempuser)
}

       

    }


    return (
        <div>
            <h4>Selecte multiple checkboxes</h4>
            <div className="form-check">
                <input
                type="checkbox"
                name="allselect"
                // checked=
                onChange={handleselect}
                />
                 <label>All Select</label>
            </div>
{
   multiplecheck.map((row,id)=>{
       return (
        <div className="form-check" key={id}>
        <input
        type="checkbox"
        name={row.city}
        checked={row? row.ischecked:false}
        onChange={handleselect}
        />
         <label>{row.city}</label>
    </div>

       )
   }) 
}
           



        </div>
    )
}

export default Selectmultiplecheckboxes
