import React, { Component } from 'react'
import Basicjavascript from './Basicjavascript'
import OOPCONCEPT from './Oop'
import Promise from './promise'
export class Index extends Component {
constructor(){
    super()
    this.state={
        activetab:0
    } 
}

activetabfun=(e,tabid)=>{
    this.setState({
        activetab:tabid
    })

}
    render() {

        const {activetab}=this.state
        return (
        <>
            <div >
                
                <h1> Advanced Javascript</h1>
         <button className="btn btn-secondary mr-2 " 
         onClick={(e)=>this.activetabfun(e,1)} style={{marginLeft:"25px"}}> Basic   </button>


   <button className="btn btn-secondary ml-2" 
         onClick={(e)=>this.activetabfun(e,2)}>OOP Concept </button>

<button className="btn btn-secondary " 
         onClick={(e)=>this.activetabfun(e,3)} style={{marginRight:"20px"}}>Promise </button>

            </div>
<br/>

{
    activetab===3&&
    <Promise/>
}


{activetab===1&&
   <Basicjavascript/>
}
{activetab===2&&
    <OOPCONCEPT/>
}


</>

        )
    }
}

export default Index
