import React, { Component } from 'react'

export class JsObject extends Component {
    constructor(){
        super()
        this.state={
            person :{
                firstname: "John",
                lastname: "Doe",
                age: 50,
                eyecolor: "blue"
              }
        }
    }
    addnewvalue=()=>{
     const persondata={...this.state.person}   
     persondata.nationality="pakistani"
     persondata.city="Islamabad"
    this.setState({
  person:persondata
    })
      
    }
    deletevalue=()=>{
        const persondata={...this.state.person} 
        delete persondata.nationality
        this.setState({
            person:persondata
        })

    }
    render() {
        const {person}=this.state
        return (
            <div>
            <p>
                {/* {
                  Object.keys(person).map(value=>{
                      return <>
                      {
                          <span>{value}</span>
                      }<br/>
                      </>
                  })
                } */}
                {
                   Object.entries(person).map(value=>{
                        return <><span className=''>{value}</span><br/></>
                    })
                }
                <button onClick={this.addnewvalue}>
                    add new value
                </button>
                <button onClick={this.deletevalue}>
                    add new value
                </button>
            </p>
            </div>
        )
    }
}

export default JsObject
