import React, { Component } from 'react'

export class promise extends Component {
    constructor(){
        super()
        this.state={
            value:true
        }
    }

promisefun=(value)=>{
    return new Promise((resolve,rejected)=>{
if(value===true){
    return resolve("promise is true")
}
if(value===false){
    return rejected("false promise")
}
    })
   
}
fullfil=(result)=>{
    console.log(result)
}
rejected=(result)=>{
    console.log(result)

}


componentDidMount(){
//    console.log(new Promise())
   console.log(this.promisefun(this.state.value))
   this.promisefun(this.state.value).then(()=>{
       return this.fullfil()
   })
   this.promisefun(this.state.value).then((value)=>{
       return this.rejected()
   })
}
    render() {
        return (
            <div>
                <h1>Promise</h1>
            </div>
        )
    }
}

export default promise
