import React,{useState,useEffect} from 'react'
let multiplecheck=[
    {id:1,city:"Karachi"},
    {id:2,city:"Islamabad"},
    {id:3,city:"Lahore"},
    {id:4,city:"Rawalpindi"},
    {id:5,city:"Peshawar"},
    {id:6,city:"Faisalabad"},
    {id:7,city:"Multan"},
]
function Selectmultiplecheckboxes() {
    const [users,setuser]=useState([])

    useEffect(()=>{
        setuser(multiplecheck)
    },[])

    const handleselect =(e)=>{
        const {name,checked}=e.target

if(name ==="allselect"){
    let tempuser = users.map((users)=>{
        return {...users,isChecked:checked};
    });
    setuser(tempuser)  
}else{
    let tempuser=users.map((users)=>
        users.name === name ? {...users,isChecked:checked}:users
    )
    setuser(tempuser)
}  
    }


    return (
        <div>
            <h4>Selecte multiple checkboxes</h4>
            <div className="form-check">
                <input
                type="checkbox"
                name="allselect"
                checked={!users.some((user) => user?.isChecked !== true)}
                onChange={handleselect}
                />
                 <label>All Select</label>
            </div>
   <div>
   {users.map((user) => (
          <div className="form-check">
            <input
              type="checkbox"
              className="form-check-input"
              name={user.city}
              checked={user?.isChecked || false}
              onChange={handleselect}
            />
            <label className="form-check-label ms-2">{user.name}</label>
          </div>
        ))}

</div>
           



        </div>
    )
}

export default Selectmultiplecheckboxes
