import React, { useMemo } from 'react'
import { useTable,useSortBy,usePagination  } from 'react-table'
import Mockdata from './MOCK_DATA (1).json'
import { COLUMN } from './column'
import './tablecss.css'

function Form() {
  const columns = useMemo(() => COLUMN, [])
  const data = useMemo(() => Mockdata, [])

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    page,
    prepareRow,
    nextPage,
    previousPage,
    canNextPage,
    canPreviousPage,
    pageOptions,
    state,


  }= useTable({columns, data  },useSortBy,usePagination)
    
 const {pageIndex}=state
  return (
    <div>
    <table {...getTableProps()}>
       <thead>
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map(column => (
                // Add the sorting props to control sorting. For this example
                // we can add them into the header props
                <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                  {column.render('Header')}
                  {/* Add a sort direction indicator */}
                  <span>
                    {column.isSorted
                      ? column.isSortedDesc
                        ? ' ðŸ”½'
                        : ' ðŸ”¼'
                      : ''}
                  </span>
                </th>
              ))}
            </tr>
          ))}
        </thead>
      <tbody {...getTableBodyProps()}>
         {page.map(row => {
           prepareRow(row)
           return (
             <tr {...row.getRowProps()}>
               {row.cells.map(cell => {
                 return (
                   <td
                     {...cell.getCellProps()}
                
                   >
                     {cell.render('Cell')}
                   </td>
                 )
               })}
             </tr>
           )
         })}
       </tbody>
       {/* <tfoot>
         {
           footerGroups.map((footerGroups)=>(
             <tr {...footerGroups.getFooterGroupProps()}>{
               footerGroups.headers.map((column)=>{
                 return <td {...column.getFooterProps}>{column.render('Footer')}</td>
               })
             }
               </tr>
           ))
         }

       </tfoot> */}
    </table>
    <div className="mt-2">
      <span> Page{' '}<strong>{pageIndex+1} of {pageOptions.length}</strong> {' '}  </span>
      <button className="btn bg-info " onClick={()=>previousPage()} disabled={!canPreviousPage}>Previous</button>
      <button className="btn bg-info ml-5" onClick={()=>nextPage()} disabled={!canNextPage}>Next</button>
    </div>
    </div>
  )
}

export default Form
